mapper.select:
SELECT *
FROM exhbn


        where exhbnTitle like CONCAT('%', #{searchValue}, '%')


        where exhbnId = #{searchValue}


        where exhbnTitle like CONCAT('%', #{searchValue}, '%')


        where exhbnType = #{exhbnType}


        where exhbnDetail like CONCAT('%', #{searchValue}, '%')


order by exhbnId desc
LIMIT #{limit}
OFFSET #{page}

mapper.update:
update exhbn
set exhbnTitle  = #{exhbnTitle},
    exhbnImgUrl = #{exhbnImgUrl}
where exhbnId = #{exhbnId}
